generated.css:
html {
  font-family: sans-serif;
}

nav {
  display: flex;
  margin: 2vmin;
  justify-content: space-around;
}

nav > a,
nav > a:visited  {
  font-weight: bold;
  color: hsl( 90, 0%, 100%);
  background-color: hsl( 90, 0%, 0%);
  padding: 2vmin;
  border-radius: 5px;
  text-decoration: none;
}

nav > a:hover {
  color: coral;
}

#appBody__container {
  display: flex;
  justify-content: center;
}

#canvasContainer {
  display: flex;
  flex-direction: column;
  margin: 1vmin;
}
#canvasContainer > div {
  display: flex;
  justify-content: space-between;
  padding: 2vmin 0;
}
form {
  width: 500px;
  background: #ccc;
  margin: 0 auto;
  padding: 20px;
  border: 1px solid black;
}

form ol {
  padding-left: 0;
}

form li, div > p {
  background: #eee;
  display: flex;
  justify-content: space-between;
  margin-bottom: 10px;
  list-style-type: none;
  border: 1px solid black;
}

form img {
  height: 64px;
  order: 1;
}

form p {
  line-height: 32px;
  padding-left: 10px;
}

form label, form button {
  background-color: #7F9CCB;
  padding: 5px 10px;
  border-radius: 5px;
  border: 1px ridge black;
  font-size: 0.8rem;
  height: auto;
}

form label:hover, form button:hover {
  background-color: #2D5BA3;
  color: white;
}

form label:active, form button:active {
  background-color: #0D3F8F;
  color: white;
}
#myCanvas{
    border: 3px dotted red;
}

#uploadImage {
  opacity: 1;
}

#originalImageContainer {
  width: 70vw;
  max-width: 350px;
}
